import React, { useState, useEffect } from "react";
import Table from "./Table";
import Form from "./Form";

function App() {
  const [tableData, setTableData] = useState([]); // Initialize with an empty array
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("http://localhost:8001/transactions")
      .then((response) => response.json())
      .then((data) => setTableData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div>
      <Form tableData={tableData} setTableData={setTableData} search={search} setSearch={setSearch} />
      {tableData ? (
        <Table tableData={tableData} search={search} setSearch={setSearch} />
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}

export default App;
