import React,{useState} from "react"

function Form({ tableData, setTableData }) {
    const [date, setDate] = useState("");
    const [description, setDescription] = useState("");
    const [category, setCategory] = useState("");
    const [amount, setAmount] = useState("");

    function handleSubmit(e) {
      e.preventDefault();

      // Create a new transaction object
      const newTransaction = {
        id: tableData.transaction.length + 1, // You might want to generate a unique ID
        date: date,
        description: description,
        category: category,
        amount: parseFloat(amount), // Convert to a number
      };

      // Update the tableData state with the new transaction
      setTableData([...tableData, newTransaction]);

      // Clear the form fields
      setDate("");
      setDescription("");
      setCategory("");
      setAmount("");
    }

    return (
      <form onSubmit={handleSubmit}>
        <h1>Add a new transaction</h1>
        <label>Add the date of the new transaction</label>
        <input type="text" value={date} onChange={(e) => setDate(e.target.value)} />
        <br></br>
        <label>Add a description of the new transaction</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <br></br>
        <label>Add category of the new transaction</label>
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <br></br>
        <label>Add amount of the new transaction</label>
        <input
          type="text"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <br></br>
        <input type="submit" value="Submit" />
        <br></br>
        <label>Search</label>
        <input type="text" />
        <br></br>
        <input type="submit" />
      </form>
    );
  }

  export default Form;
